contracts/Coinflip.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import "./Common.sol";

contract CoinFlip is Common {
    using SafeERC20 for IERC20;

    mapping(address => CoinFlipGame) coinFlipGames;
    mapping(uint256 => address) coinIDs;
    mapping(address => bool) public isTokenAllowed;

    constructor(
        uint64 subscriptionID,
        address _vrf,
        bytes32 _keyHash,
        address link_eth_feed,
        address tokenAllowed,
        address wrappedToken
    ) Common(subscriptionID, link_eth_feed, _vrf, _keyHash, wrappedToken) {
        isTokenAllowed[tokenAllowed] = true;
    }

    struct CoinFlipGame {
        uint256 wager;
        uint256 stopGain;
        uint256 stopLoss;
        uint256 requestID;
        address tokenAddress;
        uint64 blockNumber;
        uint32 numBets;
        bool isHeads;
    }

    /**
     * @dev event emitted at the start of the game
     * @param playerAddress address of the player that made the bet
     * @param wager wagered amount
     * @param tokenAddress address of token the wager was made, 0 address is considered the native coin
     * @param isHeads player bet on which side the coin will land  1-> Heads, 0 ->Tails
     * @param numBets number of bets the player intends to make
     * @param stopGain gain value at which the betting stop if a gain is reached
     * @param stopLoss loss value at which the betting stop if a loss is reached
     */
    event CoinFlip_Play_Event(
        address indexed playerAddress,
        uint256 wager,
        address tokenAddress,
        bool isHeads,
        uint32 numBets,
        uint256 stopGain,
        uint256 stopLoss,
        uint256 VRFFee
    );

    /**
     * @dev event emitted by the VRF callback with the bet results
     * @param playerAddress address of the player that made the bet
     * @param wager wager amount
     * @param payout total payout transfered to the player
     * @param tokenAddress address of token the wager was made and payout, 0 address is considered the native coin
     * @param coinOutcomes results of coinFlip, 1-> Heads, 0 ->Tails
     * @param payouts individual payouts for each bet
     * @param numGames number of games performed
     */
    event CoinFlip_Outcome_Event(
        address indexed playerAddress,
        uint256 wager,
        uint256 payout,
        address tokenAddress,
        uint8[] coinOutcomes,
        uint256[] payouts,
        uint32 numGames
    );

    /**
     * @dev event emitted when a refund is done in coin flip
     * @param player address of the player receiving the refund
     * @param wager amount of wager that was refunded
     * @param tokenAddress address of token the refund was made in
     */
    event CoinFlip_Refund_Event(
        address indexed player,
        uint256 wager,
        address tokenAddress
    );

    /**
     * @dev event emitted when a transfer of ownership is done
     * @param prevOwner address of the previous owner
     * @param newOwner address of the new owner
     */
    event Transfer_Ownership_Event(address prevOwner, address newOwner);

    /**
     * @dev event emitted when a change of subscription is done
     * @param subID number of the previous subscription
     * @param newSubID number of the new subscription
     */
    event Subscrition_Change_Event(uint64 subID, uint64 newSubID);

    /**
     * @dev event emitted when a setting of bet token is done
     * @param token bet token address
     * @param status true - bet token / false - token is not for betting
     */
    event Token_Set_Event(address token, bool status);

    /**
     * @dev event emitted when a withdrawal of token is done
     * @param to address to transfer the house edge to
     * @param amount amount to transfer
     * @param tokenAddress address of token to transfer
     */
    event Withdraw_HouseEdge_Event(
        address to,
        uint amount,
        address tokenAddress
    );

    /**
     * @dev event emitted when the contract was funded with native currency
     * @param sender refill address
     * @param amount refill amount
     */
    event Received(address sender, uint amount);

    error WagerAboveLimit(uint256 wager, uint256 maxWager);
    error AwaitingVRF(uint256 requestID);
    error InvalidNumBets(uint256 maxNumBets);
    error NotAwaitingVRF();
    error BlockNumberTooLow(uint256 have, uint256 want);
    error OnlyCoordinatorCanFulfill(address have, address want);

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    /**
     * @dev Function to play Coin Flip, takes the user wager saves bet parameters and makes a request to the VRF
     * @param wager wager amount
     * @param tokenAddress address of token to bet, 0 address is considered the native coin
     * @param numBets number of bets to make, and amount of random numbers to request
     * @param stopGain treshold value at which the bets stop if a certain profit is obtained
     * @param stopLoss treshold value at which the bets stop if a certain loss is obtained
     * @param isHeads if bet selected heads or Tails
     */
    function CoinFlip_Play(
        uint256 wager,
        address tokenAddress,
        bool isHeads,
        uint32 numBets,
        uint256 stopGain,
        uint256 stopLoss
    ) external payable nonReentrant {
        if (coinFlipGames[msg.sender].requestID != 0) {
            revert AwaitingVRF(coinFlipGames[msg.sender].requestID);
        }
        if (!(numBets > 0 && numBets <= 100)) {
            revert InvalidNumBets(100);
        }

        _kellyWager(wager, tokenAddress);
        uint256 fee = _transferWager(
            tokenAddress,
            wager * numBets,
            700000,
            msg.sender
        );

        uint256 id = _requestRandomWords(numBets);

        coinFlipGames[msg.sender] = CoinFlipGame(
            wager,
            stopGain,
            stopLoss,
            id,
            tokenAddress,
            uint64(block.number),
            numBets,
            isHeads
        );
        coinIDs[id] = msg.sender;

        emit CoinFlip_Play_Event(
            msg.sender,
            wager,
            tokenAddress,
            isHeads,
            numBets,
            stopGain,
            stopLoss,
            fee
        );
    }

    /**
     * @dev Function to refund user in case of VRF request failling
     */
    function CoinFlip_Refund() external nonReentrant {
        CoinFlipGame storage game = coinFlipGames[msg.sender];
        if (game.requestID == 0) {
            revert NotAwaitingVRF();
        }
        if (game.blockNumber + 100 > block.number) {
            revert BlockNumberTooLow(block.number, game.blockNumber + 100);
        }

        uint256 wager = game.wager * game.numBets;
        address tokenAddress = game.tokenAddress;

        delete (coinIDs[game.requestID]);
        delete (coinFlipGames[msg.sender]);

        if (tokenAddress == address(0)) {
            (bool success, ) = payable(msg.sender).call{value: wager}("");
            if (!success) {
                revert TransferFailed();
            }
        } else {
            IERC20(tokenAddress).safeTransfer(msg.sender, wager);
        }
        emit CoinFlip_Refund_Event(msg.sender, wager, tokenAddress);
    }

    /**
     * @dev transfers house edge from game contract
     * Can only be called by owner
     * @param to address to transfer the house edge to
     * @param amount amount to transfer
     * @param tokenAddress address of token to transfer
     */
    function withdrawHouseEdge(
        address to,
        uint amount,
        address tokenAddress
    ) external onlyOwner {
        emit Withdraw_HouseEdge_Event(to, amount, tokenAddress);
        _transferHouseEdgePvP(to, amount, tokenAddress);
    }

    /**
     * @dev function to transfer ownership
     * Can only be called by owner
     * @param newOwner new owner address
     */
    function transferOwnership(address newOwner) external onlyOwner {
        emit Transfer_Ownership_Event(owner, newOwner);
        _transferOwnership(newOwner);
    }

    /**
     * @dev function to change Chainlink VRF subscription ID
     * Can only be called by owner
     * @param _subscriptionId new Chainlink VRF subscription ID
     */
    function changeSubscription(uint64 _subscriptionId) external onlyOwner {
        emit Subscrition_Change_Event(s_subscriptionId, _subscriptionId);
        _changeSubscription(_subscriptionId);
    }

    /**
     * @dev function to add/remove bet token
     *     Can only be called by owner
     * @param token bet token address
     * @param status true - bet token / false - token is not for betting
     */
    function setToken(address token, bool status) external onlyOwner {
        emit Token_Set_Event(token, status);
        isTokenAllowed[token] = status;
    }

    /**
     * @dev function to get current request player is await from VRF, returns 0 if none
     * @param player address of the player to get the state
     */
    function CoinFlip_GetState(
        address player
    ) external view returns (CoinFlipGame memory) {
        return (coinFlipGames[player]);
    }

    /**
     * @dev function called by Chainlink VRF with random numbers
     * @param requestId id provided when the request was made
     * @param randomWords array of random numbers
     */
    function rawFulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) external {
        if (msg.sender != address(COORDINATOR)) {
            revert OnlyCoordinatorCanFulfill(msg.sender, address(COORDINATOR));
        }
        fulfillRandomWords(requestId, randomWords);
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal {
        address playerAddress = coinIDs[requestId];
        if (playerAddress == address(0)) revert();
        CoinFlipGame storage game = coinFlipGames[playerAddress];
        if (block.number > game.blockNumber + 100) revert();
        int256 totalValue;
        uint256 payout;
        uint32 i;
        uint8[] memory coinFlip = new uint8[](game.numBets);
        uint256[] memory payouts = new uint256[](game.numBets);

        address tokenAddress = game.tokenAddress;

        for (i = 0; i < game.numBets; i++) {
            if (totalValue >= int256(game.stopGain)) {
                break;
            }
            if (totalValue <= -int256(game.stopLoss)) {
                break;
            }

            coinFlip[i] = uint8(randomWords[i] % 2);

            if (coinFlip[i] == 1 && game.isHeads == true) {
                totalValue += int256((game.wager * 9800) / 10000);
                payout += (game.wager * 19800) / 10000;
                payouts[i] = (game.wager * 19800) / 10000;
                continue;
            }
            if (coinFlip[i] == 0 && game.isHeads == false) {
                totalValue += int256((game.wager * 9800) / 10000);
                payout += (game.wager * 19800) / 10000;
                payouts[i] = (game.wager * 19800) / 10000;
                continue;
            }

            totalValue -= int256(game.wager);
        }

        payout += (game.numBets - i) * game.wager;

        emit CoinFlip_Outcome_Event(
            playerAddress,
            game.wager,
            payout,
            tokenAddress,
            coinFlip,
            payouts,
            i
        );
        delete (coinIDs[requestId]);
        delete (coinFlipGames[playerAddress]);
        if (payout != 0) {
            _transferPayout(playerAddress, payout, tokenAddress);
        }
    }

    /**
     * @dev calculates the maximum wager allowed based on the bankroll size
     */
    function _kellyWager(uint256 wager, address tokenAddress) internal view {
        uint256 balance;
        if (tokenAddress == address(0)) {
            balance = address(this).balance;
        } else {
            if (isTokenAllowed[tokenAddress] == false) {
                revert InvalidToken();
            }
            balance = IERC20(tokenAddress).balanceOf(address(this));
        }
        uint256 maxWager = (balance * 11) / 1000;
        if (wager > maxWager) {
            revert WagerAboveLimit(wager, maxWager);
        }
    }
}

contracts/Common.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IVRFCoordinatorV2 is VRFCoordinatorV2Interface {
    function getFeeConfig()
        external
        view
        returns (
            uint32,
            uint32,
            uint32,
            uint32,
            uint32,
            uint24,
            uint24,
            uint24,
            uint24
        );
}

contract Common is ReentrancyGuard {
    using SafeERC20 for IERC20;

    uint256 public VRFFees;
    uint64 public s_subscriptionId;
    address public owner;
    address public wrappedToken;

    uint32 internal constant CALLBACK_GAS_LIMIT = 2500000;
    uint16 internal constant REQUEST_CONFIRMATIONS = 3;

    AggregatorV3Interface public immutable LINK_ETH_FEED;
    IVRFCoordinatorV2 public immutable COORDINATOR;
    bytes32 internal immutable KEY_HASH;

    constructor(
        uint64 subscriptionId,
        address link_eth_feed,
        address _vrf,
        bytes32 _keyHash,
        address _wrappedToken
    ) {
        COORDINATOR = IVRFCoordinatorV2(_vrf);
        LINK_ETH_FEED = AggregatorV3Interface(link_eth_feed);
        s_subscriptionId = subscriptionId;
        owner = msg.sender;
        KEY_HASH = _keyHash;
        wrappedToken = _wrappedToken;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    error InvalidValue(uint256 required, uint256 sent);
    error TransferFailed();
    error RefundFailed();
    error NotOwner(address want, address have);
    error ZeroWager();
    error InvalidToken();

    /**
     * @dev function to transfer VRF fees acumulated in the contract
     * Can only be called by owner
     */
    function transferFees(address to) external nonReentrant onlyOwner {
        uint256 fee = VRFFees;
        VRFFees = 0;
        (bool success, ) = payable(to).call{value: fee}("");
        if (!success) {
            revert TransferFailed();
        }
    }

    /**
     * @dev calculates in form of native token the fee charged by chainlink VRF
     * @return fee amount of fee user has to pay
     */
    function getVRFFee(uint256 gasAmount) public view returns (uint256 fee) {
        (, int256 answer, , , ) = LINK_ETH_FEED.latestRoundData();
        (uint32 fulfillmentFlatFeeLinkPPMTier1, , , , , , , , ) = COORDINATOR
            .getFeeConfig();

        fee =
            (tx.gasprice * 18 * gasAmount) /
            10 +
            ((1e12 *
                uint256(fulfillmentFlatFeeLinkPPMTier1) *
                uint256(answer)) / 1e18);
    }

    /**
     * @dev function to transfer the player wager to game contract
     * , reverts if wager is zero
     * @param tokenAddress address of the token the wager is made on
     * @param wager total amount wagered
     * @param msgSender player address
     */

    function _transferWager(
        address tokenAddress,
        uint256 wager,
        uint256 gasAmount,
        address msgSender
    ) internal returns (uint256 VRFfee) {
        if (wager == 0) {
            revert ZeroWager();
        }
        VRFfee = getVRFFee(gasAmount);
        if (tokenAddress == address(0)) {
            if (msg.value < wager + VRFfee) {
                revert InvalidValue(wager + VRFfee, msg.value);
            }
            _refundExcessValue(msg.value - (VRFfee + wager));
        } else {
            if (msg.value < VRFfee) {
                revert InvalidValue(VRFfee, msg.value);
            }

            IERC20(tokenAddress).safeTransferFrom(
                msgSender,
                address(this),
                wager
            );

            _refundExcessValue(msg.value - VRFfee);
        }
        VRFFees += VRFfee;
    }

    /**
     * @dev returns to user the excess fee sent to pay for the VRF
     * @param refund amount to send back to user
     */
    function _refundExcessValue(uint256 refund) internal {
        if (refund == 0) {
            return;
        }
        (bool success, ) = payable(msg.sender).call{value: refund}("");
        if (!success) {
            revert RefundFailed();
        }
    }

    /**
     * @dev transfers payout from the game contract to the players
     * @param player address of the player to transfer the payout to
     * @param payout amount of payout to transfer
     * @param tokenAddress address of the token that payout will be transfered
     */
    function _transferPayout(
        address player,
        uint256 payout,
        address tokenAddress
    ) internal {
        if (tokenAddress == address(0)) {
            (bool success, ) = payable(player).call{value: payout}("");
            if (!success) {
                (bool _success, ) = wrappedToken.call{value: payout}(
                    abi.encodeWithSignature("deposit()")
                );
                if (!_success) {
                    revert TransferFailed();
                }
                IERC20(wrappedToken).safeTransfer(player, payout);
            }
        } else {
            IERC20(tokenAddress).safeTransfer(player, payout);
        }
    }

    /**
     * @dev transfers house edge from game contract
     * @param to address to transfer the house edge to
     * @param amount amount to transfer
     * @param tokenAddress address of token to transfer
     */
    function _transferHouseEdgePvP(
        address to,
        uint256 amount,
        address tokenAddress
    ) internal {
        if (tokenAddress == address(0)) {
            (bool success, ) = payable(to).call{value: amount}("");
            if (!success) {
                revert TransferFailed();
            }
        } else {
            IERC20(tokenAddress).safeTransfer(to, amount);
        }
    }

    /**
     * @dev function to transfer ownership
     * @param newOwner new owner address
     */
    function _transferOwnership(address newOwner) internal {
        owner = newOwner;
    }

    /**
     * @dev function to change Chainlink VRF subscription ID
     * @param _subscriptionId new Chainlink VRF subscription ID
     */
    function _changeSubscription(uint64 _subscriptionId) internal {
        s_subscriptionId = _subscriptionId;
    }

    /**
     * @dev function to send the request for randomness to chainlink
     * @param numWords number of random numbers required
     */
    function _requestRandomWords(
        uint32 numWords
    ) internal returns (uint256 requestId) {
        requestId = COORDINATOR.requestRandomWords(
            KEY_HASH,
            s_subscriptionId,
            REQUEST_CONFIRMATIONS,
            CALLBACK_GAS_LIMIT,
            numWords
        );
        return requestId;
    }
}

contracts/testContracts/ContractConsumer.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

interface ICoinFlip {
    function CoinFlip_Play(
        uint256,
        address,
        bool,
        uint32,
        uint256,
        uint256
    ) external payable;
}

contract Consumer {
    ICoinFlip public coinflip;
    address public allowedDepositor;
    bool public isRefund = true;

    constructor(address _coinflip) {
        coinflip = ICoinFlip(_coinflip);
        allowedDepositor = msg.sender;
    }

    error InvalidDepositor();

    receive() external payable {
        if (isRefund) {
            isRefund = false;
        } else revert InvalidDepositor();
    }

    function placeBet(
        uint256 wager,
        address tokenAddress,
        bool isHeads,
        uint32 numBets,
        uint256 stopGain,
        uint256 stopLoss
    ) public payable {
        coinflip.CoinFlip_Play{value: msg.value}(
            wager,
            tokenAddress,
            isHeads,
            numBets,
            stopGain,
            stopLoss
        );
    }
}

