pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract TestToken is ERC20 {
    constructor(address[] memory mintTo) ERC20("MyToken", "MTK") {
        for (uint i; i < mintTo.length; i++) {
